mapper.select:
SELECT *
FROM habit
WHERE uid = #{uid}
AND habit_id = #{habitId}

SELECT icon, date
FROM habit_records
WHERE habit_id = #{habitId}
AND date BETWEEN #{startDate} AND #{endDate}

mapper.insert:
INSERT INTO habit (uid, title, description, icon, date)
VALUES (#{uid}, #{title}, #{description}, '', DATE_FORMAT(NOW(), '%Y%m%d'));

    SELECT LAST_INSERT_ID() AS habitId
